contentStructures.style:
@page {
    size: 210mm 297mm;
    margin: 16mm 10mm 30mm 12mm;@bottom-center {
    tvertical-align: top;
    tcontent: element(footer);
}

body {
    font-size: 12px;
    font-family: Calibri, Arial, sans-serif;
    margin: 0;
}

.header {
    margin: 4mm 0 0 11mm;
    height: 80mm;
    width: 100mm;
    float: left;
}

.asiaotsikko {
    clear: both;
}

.sisalto {
    clear: both;
    line-height: 140%;
}

table {
    border-collapse: collapse;
}

td, th {
    border: 1px solid gray;
    padding: 5px;
}

td {
    text-align: center;
}

th {
    vertical-align: top;
    text-align: center;
}

ol, ul {
    padding-top: 0;
    margin-top: 0;
    font-weight: 100;
    padding-left: 4mm;
    margin-left: 2em;
}

ul.dash {
    list-style: none;
    margin-left: 0;
    padding-left: 1em;
}

ul.dash li:before {
    display: inline-block;
    content: "-";
    width: 1em;
    margin-left: -1em;
}

.footer {
    font-size: 90%;
    position: running(footer);
}

.footer .heading {
    width: 100%;
}

.footer div {
    float: left;
    width: 30mm;
}
